mapper.update:
UPDATE PROGRAM SET PROCATE=#{proCate}, RECSTATUS=#{recStatus}, PROTITLE=#{proTitle}, 
				   PROCONTENTS=#{proContents}, TARGET=#{target}, TOTAL=#{total}, 
				   PRSDATE=#{prsDate}, PRLDATE=#{prlDate}, PSDATE=#{psDate}, 
				   PLDATE=#{plDate}, PROTEACHER=#{proTeacher}
WHERE PRONUM=#{proNum}

mapper.select:
SELECT PRONUM, PROCATE, RECSTATUS, PROTITLE, TARGET, TOTAL, PRSDATE, PRLDATE, DATE_FORMAT(PSDATE,'%Y-%m-%d %H:%i') as psDt, DATE_FORMAT(PLDATE,'%Y-%m-%d %H:%i') as plDt, PROTEACHER, PROPLACE FROM PROGRAM
ORDER BY PRONUM DESC
limit #{startRow}, #{perPage};
