mapper.update:
UPDATE BOOK SET BOOKCOUNT
	= (SELECT BOOKCOUNT FROM BOOK WHERE ISBN = #{isbn})+1
WHERE ISBN = #{isbn}

mapper.select:
SELECT COUNT(LOANNUM) FROM LOAN
WHERE USERNAME = #{userName} AND RTSTATUS = #{rtStatus}

		AND ISBN = #{isbn}

SELECT L.*, B.* FROM LOAN L INNER JOIN BOOK B ON B.ISBN = L.ISBN
WHERE L.USERNAME = #{userName} AND RTSTATUS = #{rtStatus}
AND B.TITLE LIKE CONCAT ('%', #{search}, '%') AND B.WRITER LIKE CONCAT ('%', #{search}, '%')
AND L.REGDATE BETWEEN #{dateA} AND #{dateB}
ORDER BY
B.TITLE B.ISBN L.LOANNUM
DESC ASC
LIMIT #{startRow}, #{perPage};

SELECT E.*, B.* FROM RESERVATION E INNER JOIN BOOK B ON B.ISBN = E.ISBN
WHERE E.USERNAME = #{userName}
AND B.TITLE LIKE CONCAT ('%', #{search}, '%') AND B.WRITER LIKE CONCAT ('%', #{search}, '%')
AND E.REGDATE BETWEEN #{dateA} AND #{dateB}
ORDER BY
B.TITLE B.ISBN E.RVNUM
DESC ASC
LIMIT #{startRow}, #{lastRow}

SELECT M.*, b.* FROM MUTUALLOAN M INNER JOIN BOOK B ON B.ISBN = M.ISBN
WHERE M.USERNAME = #{userName}
AND B.TITLE LIKE CONCAT ('%', #{search}, '%') AND B.WRITER LIKE CONCAT ('%', #{search}, '%')
AND M.REGDATE BETWEEN #{dateA} AND #{dateB}
ORDER BY
B.TITLE B.ISBN M.MUNUM
DESC ASC
LIMIT #{startRow}, #{lastRow}
