mapper.update:
UPDATE PROGRAM SET PROCATE=#{proCate}, RECSTATUS=#{recStatus}, PRONAME=#{proName}, TARGET=#{target}, TOTAL=#{total}, 
				   PRSDATE=#{prsDate}, PRLDATE=#{prlDate}, PSDATE=#{psDate}, 
				   PLDATE=#{plDate}, PROTEACHER=#{proTeacher}
WHERE PRONUM=#{proNum}

mapper.select:
SELECT * FROM PROGRAM
ORDER BY PRONUM DESC
limit #{startRow}, #{perPage};
